import React from 'react';
import { ActionIcon, useMantineColorScheme } from '@mantine/core';
import { IconSun, IconMoonStars } from '@tabler/icons';
import { createStyles, Header, Container, Text, Button, Title, useMantineTheme, Menu, Box, NavLink  } from '@mantine/core';
import { Link } from "react-router-dom";
import {
    IconSquareCheck,
    IconPackage,
    IconUsers,
    IconCalendar,
    IconChevronDown,
  } from '@tabler/icons';
// import { HomeSignal } from 'tabler-icons-react';


const useStyles = createStyles((theme) => ({
header: {
    backgroundColor: theme.fn.variant({ variant: 'filled', color: theme.primaryColor }).background,
    borderBottom: 0,
},

inner: {
    height: 0,
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
},

}));



export default function HeaderMenuColored({loggedInUser}) {
    const { classes } = useStyles();

    // const profileUrl = `/${loggedInUser.username}`;

    return (
        <>
        <Header height={80} className={classes.header}>
            <Menu transition="pop-top-right" position="top-end" width={220}>
                <Menu.Target>
                    <Button rightIcon={<IconChevronDown size={18} stroke={1.5} />} pr={12}>
                        Create new
                    </Button>
                </Menu.Target>
            <Menu.Dropdown>
            <Menu.Item
                icon={<IconPackage size={16} color="blue" stroke={1.5} />}
                rightSection={
                <Text size="xs" transform="uppercase" weight={700} color="dimmed">
                    Ctrl + P
                </Text>
            }
        >
            Project
            </Menu.Item>
            <Menu.Item
                icon={<IconSquareCheck size={16} color="blue" stroke={1.5} />}
                rightSection={
                <Text size="xs" transform="uppercase" weight={700} color="dimmed">
                    Ctrl + T
                </Text>
            }
        >
            Task
            </Menu.Item>
            <Menu.Item
                icon={<IconUsers size={16} color="blue" stroke={1.5} />}
                rightSection={
                <Text size="xs" transform="uppercase" weight={700} color="dimmed">
                Ctrl + U
                </Text>
            }
        >
            Team
            </Menu.Item>
            <Menu.Item
                icon={<IconCalendar size={16} color="blue" stroke={1.5} />}
                rightSection={
                <Text size="xs" transform="uppercase" weight={700} color="dimmed">
                Ctrl + E
                </Text>
            }
        >
            Event
            </Menu.Item>
            </Menu.Dropdown>
        </Menu>
        </Header>
        </>
    );
}
