import { createStyles } from '@mantine/styles';

export default createStyles((theme) => ({
  placeholder: {
    color: theme.colorScheme === 'dark' ? theme.colors.dark[3] : theme.colors.gray[5],
  },

  input: {
    cursor: 'pointer',
  },
}));
