import React, {useState, useEffect} from 'react';
import { FileInput, Textarea, TextInput, Button, Modal, Group, SimpleGrid, Text, Image } from '@mantine/core';
import { useNavigate } from "react-router-dom";
import Header from './Header';


export default function AddPlaylist({handleAddPost}) {

const navigate = useNavigate()

const RAPID_API_KEY = process.env.RAPID_API_KEY // NOT WORKING!

const [state, setState] = useState({
    songName: '',
})

let queryString = {q: state.songName}

const axios = require("axios")
const [songInfo, setSongInfo] = useState({});

const options = {
    method: 'GET',
    url: 'https://genius.p.rapidapi.com/search',
    params: queryString,
    headers: {
    'X-RapidAPI-Key': '355f8f5ff2msh84cc656855492b7p1b752cjsn934e4aa88371',
    'X-RapidAPI-Host': 'genius.p.rapidapi.com'
    }
};

function handleSubmit(e) {
    e.preventDefault();

    axios.request(options).then(function (response) {

        setSongInfo(response.data.response.hits[0].result)
        console.log(songInfo);

        const optionsTwo = {
            method: 'GET',
            url: `https://genius.p.rapidapi.com/songs/${response.data.response.hits[0].result.id}`,
            headers: {
            'X-RapidAPI-Key': '355f8f5ff2msh84cc656855492b7p1b752cjsn934e4aa88371',
            'X-RapidAPI-Host': 'genius.p.rapidapi.com'
            }
        }

        axios.request(optionsTwo).then(function (response) {
            console.log(response);
        })


    }).catch(function (error) {
        console.error(error);
    });
}

function handleChange(e) {
    setState({
        ...state,
        [e.target.name]: e.target.value
    })
}

    return(

        <>
        <SimpleGrid col={3}>
            <Header />
        <div style={{margin: '5% 40% 0 40%'}}>
        <form autoComplete="off" onSubmit={handleSubmit}>

            <TextInput
                style={{maxWidth: 350}}
                label="Search Song"
                className="form-one"
                name="songName"
                value={state.songName}
                onChange={handleChange}
                required
                withAsterisk
                placeholder="Song Title"
            />

            <br />

            <div style={{margin: '0 35% 0 35%'}}>
            <Button
                type="submit"
                className='button'
                onClick={handleSubmit}
            >
            Search 
            </Button>
            </div>
            <div style={{ width: 300, marginLeft: 'auto', marginRight: 'auto' }}>

                <br />
        <Image
        radius="md"
        src="https://images.unsplash.com/photo-1627552245715-77d79bbf6fe2?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=640&q=80"
        alt="Random unsplash image"
        caption="My dog begging for treats"
        />
    </div>
            </form>

            </div>
            </SimpleGrid>
        </>
    )
}
