import React, {useState} from 'react';
import { Input, FileInput, Textarea, Button } from '@mantine/core';
import TextareaAutosize from 'react-textarea-autosize';
export default function AddPlaylist() {

const [state, setState] = useState({
    playlistName: '',
    tracklist: ''
})

const [selectedCover, setSelectedCover] = useState('');


function handleFileInput(e) {
    setSelectedCover(e.target.files[0])
}

function handleSubmit(e) {

}

function handleChange(e) {
    setState({
        ...state,
        [e.target.name]: e.target.value
    })
}




    return(

    <form autoComplete="off" onSubmit={handleSubmit}>

        <Input.Wrapper
            className="form-one"
            name="playlistName"
            value={state.playlistName}
            onChange={handleChange}
            label="What is your Playlist called?"
            withAsterisk
        >
        <Input placeholder="Playlist Title" />

        </Input.Wrapper>

        <br />

        <FileInput
            className="form-control"
            name="playlist-cover"
            type="file"
            placeholder="Choose File"
            onChange={handleFileInput}
            label="Playlist Cover"
            withAsterisk
        />

        <br />

        <Textarea
            className="form"
            name="tracklist"
            value={state.tracklist}
            placeholder="Enter Tracklist"
            onChange={handleChange}
            label="Your Playlist's Tracklist:"
            autosize
            withAsterisk
        />

        <br/>

        <Button
            type="submit"
            className='button'
        >
            Post Playlist
        </Button>

    </form>

    )
}
