import React from 'react';
import { ActionIcon, useMantineColorScheme } from '@mantine/core';
import { IconSun, IconMoonStars } from '@tabler/icons';
import { createStyles, Header, Container, Text, Button } from '@mantine/core';
import { Link } from "react-router-dom";
// import { HomeSignal } from 'tabler-icons-react';


const useStyles = createStyles((theme) => ({
header: {
    backgroundColor: theme.fn.variant({ variant: 'filled', color: theme.primaryColor }).background,
    borderBottom: 0,
},

inner: {
    height: 0,
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
},

}));



export default function HeaderMenuColored({loggedInUser}) {
    const { classes } = useStyles();

    const profileUrl = `/${loggedInUser.username}`;

    return (
        <Header height={50} className={classes.header}>
            {/* <HomeSignal
                size={40}
                strokeWidth={2}
                color={'white'}
            /> */}

            <div style={{margin: '0 0 30% 80%'}}>
            <Link to={profileUrl}>
            <Button size='md'>
            My Profile
            </Button>
            </Link>
            <Button size='md'>
            Logout
            </Button>
            </div>
    </Header>
    );
}
